CREATE DATABASE `dev_wanzhu_weigame`;
use dev_wanzhu_weigame;

CREATE TABLE `wg_domain` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '域名自增id',
  `category` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '集合页游戏分类',
  `address` varchar(50) NOT NULL DEFAULT '' COMMENT '域名地址',
  `status` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '状态标识0.已上线的,1.没用过的,2.已下线的',
  `level` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '使用级别0.风险低,1.新游戏,2.风险高',
  `expiring_date` varchar(50) NOT NULL DEFAULT '' COMMENT '到期时间（备注）',
  `remarks` varchar(100) NOT NULL DEFAULT '' COMMENT '后台备注',
  `created_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '创建时间',
  `updated_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '更新时间',
  PRIMARY KEY (`id`),
  KEY `idx_sts_upt_level` (`status`,`updated_time`,`level`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=0 DEFAULT CHARSET=utf8 COMMENT='后台小游戏域名管理';

CREATE TABLE `wg_groups` (
  `level` tinyint(1) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `name` varchar(100) NOT NULL DEFAULT '' COMMENT '分组名称',
  `is_twodomain` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否开启泛域名，0.不开启，1.开启',
  `is_random` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否开启随机域名，0.不开启，1.开启',
  `domain_cycle_lens` smallint(2) unsigned NOT NULL DEFAULT '0' COMMENT '随机域名规则的域名数量',
  `domain_cycle_times` smallint(3) unsigned NOT NULL DEFAULT '0' COMMENT '随机域名规则的轮换时间',
  `domain_level_uris` varchar(100) NOT NULL DEFAULT '' COMMENT '规则链接',
  `remark` varchar(255) NOT NULL DEFAULT '' COMMENT '分组备注',
  `baidu_code` varchar(255) NOT NULL DEFAULT '' COMMENT '百度统计code',
  `created_time` int(10) unsigned NOT NULL COMMENT '创建时间',
  `updated_time` int(10) unsigned NOT NULL COMMENT '更新时间',
  PRIMARY KEY (`level`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='分组管理';

