CREATE TABLE `wg_mp_domain` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `mp_id` int(10) unsigned NOT NULL COMMENT '自定义的公众号ID',
  `type` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '状态标识：0.jssdk域名，1.授权域名，2支付域名',
  `domain_address` varchar(50) NOT NULL COMMENT '域名地址',
  `status` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '状态标识：0.已上线，1.已下线',
  `created_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '创建时间',
  `updated_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '更新时间',
  PRIMARY KEY (`id`),
  KEY `idx_domain_address` (`domain_address`),
  KEY `idx_mpid_type` (`mp_id`,`type`)
) ENGINE=InnoDB AUTO_INCREMENT=0 DEFAULT CHARSET=utf8 COMMENT='小游戏微信公众号域名表';

CREATE TABLE `wg_mp_info` (
  `mp_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '自定义的公众号ID',
  `mp_name` varchar(50) NOT NULL COMMENT '公众号名称',
  `type` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '公众号是否有支付功能，0.无支付，1.有支付',
  `app_id` varchar(30) NOT NULL COMMENT '应用ID',
  `app_secret` varchar(32) NOT NULL COMMENT '应用密钥',
  `created_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '创建时间',
  `updated_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '更新时间',
  PRIMARY KEY (`mp_id`)
) ENGINE=InnoDB AUTO_INCREMENT=0 DEFAULT CHARSET=utf8 COMMENT='小游戏微信公众号信息表';
